#include "gtest/gtest.h"
#include "../src/taller.h"

TEST(minimoProductoEscalar_tests, small) {
    int a1[3] = {1, 3, -5};
    int a2[3] = {-2, 4, 1};
    int b1[5] = {1, 2, 3, 4, 5};
    int b2[5] = {1, 0, 1, 0, 1};
    int c1[8] = {-190, -287, -316, 536, 55, 26, -958, 349};
    int c2[8] = {-482, -25, -922, 90, 200, 121, -102, -242};
    int x[1] = {0};
    EXPECT_EQ(-25, minimoProductoEscalar(a1,a2,3));
    EXPECT_EQ(6, minimoProductoEscalar(b1,b2,5));
    EXPECT_EQ(-929288, minimoProductoEscalar(c1,c2,8));
    EXPECT_EQ(0, minimoProductoEscalar(x,x,1));
}

